INSERT INTO `question_master2` (`course_code`, `CID`, `mo`, `co`, `kl`, `marks`, `question`, `r1`, `remarks1`, `r2`, `remarks2`, `r3`, `remarks3`, `final`, `q_id`, `question_number`, `qno`, `key1`, `scheme`, `cat2`, `ete`, `IA`) VALUES
('18EC751', '', 'Module 1', 'CO  1', 'KL 4', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Analyze Modulation and discuss on the need for modulation.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2288, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Analyze Modulation </span></span></p>\r\n\r\n<p><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Discuss on the need for modulation</span></span></p>\r\n', '<span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Modulation is defined as the process of </span><span style="color:#0070c0">changing one or more characteristics of the high-frequency carrier signal </span><span style="color:black">in proportion with the </span><span style="color:#0070c0">instantaneous value of the analog information </span><span style="color:black">(or digital data) signal.</span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">low-frequency information signal </span><span style="color:black">is superimposed over a high-frequency carrier signal in the process of modulation.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-family:Wingdings; font-size:12px">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;; font-size:12px"><span style="color:#0070c0">Modulation translates a low-frequency signal</span><span style="color:black"> to the pass band of communication channel.</span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Analog Modulation</strong></span></span></span></p>\r\n\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">When the </span><span style="color:#0070c0">carrier signal </span><span style="color:black">is analog in nature such as sinusoidal signal, the process of modulation is known as </span><span style="color:#0070c0">analog modulation</span><span style="color:black">. It is also called </span><span style="color:#0070c0">continuous wave (CW) modulation</span><span style="color:black">.</span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Analog modulation technique involves the modulation of a </span><span style="color:#0070c0">time-varying sinusoidal carrier signal by analog information signal.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><strong style="color:black; font-family:&quot;Times New Roman&quot;; font-size:12px">Bandpass Communication</strong></p>\r\n\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Electronic communication using </span><span style="color:#0070c0">modulation to shift the frequency spectrum of baseband signal to higher frequency spectrum </span><span style="color:black">is termed as bandpass</span></span> <span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">communication or carrier communication.</span></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Modulation and Demodulation</strong></span></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="color:black; font-family:&quot;Times New Roman&quot;; font-size:12px">The process of modifying </span><span style="color:#0070c0; font-family:&quot;Times New Roman&quot;; font-size:12px">baseband frequencies </span><span style="color:black; font-family:&quot;Times New Roman&quot;; font-size:12px">so that signals may be</span><span style="color:#0070c0; font-family:&quot;Times New Roman&quot;; font-size:12px"> transmitted or &lsquo;communicated&rsquo; </span><span style="color:black; font-family:&quot;Times New Roman&quot;; font-size:12px">is called modulation (at transmitter end) and demodulation (at receiver end).</span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Demodulation</span><span style="color:black"> is the reverse process at the receivers, </span><span style="color:#0070c0">removing a high-frequency carrier signal </span><span style="color:black">and detecting a low-frequency information signal.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">carrier signal </span><span style="color:black">is an essentially </span><span style="color:#0070c0">high-frequency analog signal </span><span style="color:black">so that the wireless transmission is possible using </span><span style="color:#0070c0">suitable size and type of antennas </span><span style="color:black">for wireless communications.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Need of Modulation</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is </span><span style="color:#0070c0">virtually impossible </span><span style="color:black">to transmit </span><span style="color:#0070c0">baseband signals </span><span style="color:black">through wireless medium because the size of the required </span><span style="color:#0070c0">antennas would be impractically large</span><span style="color:black">.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Modulation makes the </span><span style="color:#0070c0">designing and processing of signal</span><span style="color:black"> in transmitter and receiver devices much more convenient and simple.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Modulation allows </span><span style="color:#0070c0">frequency division multiplexing </span><span style="color:black">(FDM) for simultaneous transmission of many baseband signals over a common channel having much </span><span style="color:#0070c0">wider bandwidth</span><span style="color:black">.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Modulated signals can </span><span style="color:#0070c0">minimize</span><span style="color:black"> the effects of </span><span style="color:#0070c0">noise</span><span style="color:black"> and distortion introduced in the communications channel.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Types of Modulation</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.4pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The process of modulation may be </span><span style="color:#0070c0">analog, pulse or digital</span><span style="color:black"> in nature. The modulating or information signal may be </span><span style="color:#0070c0">analog or digital signal. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.4pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Similarly the </span><span style="color:#0070c0">carrier signal may also be of analog or digital signal</span><span style="color:black">. Accordingly, modulation technique can be broadly classified as </span><span style="color:#0070c0">analog, pulse, or digital modulation technique</span><span style="color:black">. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.4pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Various types of modulation techniques can be described as:</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Analog Modulation</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.4pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">When the </span><span style="color:#0070c0">modulating signal as well as the carrier signal is analog </span><span style="color:black">in nature, the process of modulation is known as analog modulation.</span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.4pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Amplitude modulation (AM), frequency modulation (FM) and phase modulation (PM) </span><span style="color:black">are examples of analog modulation.</span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.4pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">These correspond to </span><span style="color:#0070c0">variation of amplitude, frequency or phase </span><span style="color:black">of the carrier signal in proportion to the instantaneous value of the analog modulating signal.</span></span> </span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.4pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Pulse Modulation</strong></span></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-family:Arial; font-size:12px">&bull;</span><span style="font-family:&quot;Times New Roman&quot;; font-size:12px"><span style="color:black">When the modulating signal is </span><span style="color:#0070c0">analog in nature and the carrier signal is a periodic sequence of rectangular pulse waveform</span><span style="color:black">, the process of modulation is known as pulse modulation.</span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Pulse amplitude modulation (PAM), pulse width modulation (PWM) and pulse position modulation (PPM) </span><span style="color:black">are examples of analog pulse modulation.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">These correspond to variation of </span><span style="color:#0070c0">amplitude, width or relative position of the carrier pulse signal in proportion to the instantaneous value of the analog modulating signal.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The digital form of </span><span style="color:#0070c0">pulse modulation </span><span style="color:black">is known as pulse code modulation (PCM).</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Digital Modulation</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">modulating signal is digital </span><span style="color:black">in nature and the </span><span style="color:#0070c0">carrier signal is analog </span><span style="color:black">in nature, the process of modulation is known as </span><span style="color:#0070c0">digital modulation</span><span style="color:black">.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Amplitude Shift Keying (ASK), Frequency Shift Keying (FSK), and Phase Shift Keying (PSK) </span><span style="color:black">are examples of digital modulation.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">These correspond to </span><span style="color:#0070c0">variation of amplitude, frequency or phase of the carrier signal</span><span style="color:black"> in proportion to the digital modulating signal.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">When the </span><span style="color:#0070c0">amplitude and phase of the analog carrier signal </span><span style="color:black">are </span><span style="color:#0070c0">varied together in proportion </span><span style="color:black">to the digital modulating signal, it is known as </span><span style="color:#0070c0">Quadrature Amplitude Modulation (QAM).</span></span></span></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n', '', '', '1IA'),
('18EC751', '', 'Module 1', 'CO  1', 'KL 3', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Illustrate the concept behind the frequency translation. </span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2289, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Concept behind the frequency translation</span></span></p>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">It is </span><span style="color:#0070c0">impractical</span><span style="color:black"> to propagate </span><span style="color:#0070c0">baseband signals </span><span style="color:black">directly over standard transmission media.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">The </span><span style="color:#0070c0">processing of a signal </span><span style="color:black">in a communication system is generally </span><span style="color:#0070c0">convenient and advantageous </span><span style="color:black">by translating each </span><span style="color:#0070c0">baseband signal </span><span style="color:black">to a </span><span style="color:#0070c0">higher-frequency spectrum.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">Enable simultaneous transmission of </span><span style="color:#0070c0">multiple baseband signals</span><span style="color:black"> using common channel by translating them to different regions of frequency spectrum.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="color:black"><strong>Linear and Nonlinear Mixing</strong></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="color:black">The process of </span><span style="color:#0070c0">combining two or more signals is termed mixing</span><span style="color:black">.</span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><strong style="color:black; font-size:12px">Linear Mixing</strong></p>\r\n\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="color:#0070c0">Two or more signals are combined </span><span style="color:black">in a </span><span style="color:#0070c0">linear device </span><span style="color:black">such as a </span><span style="color:#0070c0">passive network </span><span style="color:black">or a </span><span style="color:#0070c0">small signal amplifier</span><span style="color:black">.</span></span></p>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:black">There is </span><span style="color:#0070c0">a linear relationship </span><span style="color:black">between the </span><span style="color:#0070c0">input and output.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:black">The output is simply the </span><span style="color:#0070c0">linear addition of input signals</span><span style="color:black">.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:#0070c0">No additional frequencies </span><span style="color:black">are generated in linear mixing.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="color:black"><strong>Nonlinear Mixing</strong></span></span></p>\r\n\r\n<p style="margin-left:0in; text-align:justify"><span style="color:#0070c0; font-size:12px">Two or more signals are combined in a nonlinear device </span><span style="color:black; font-size:12px">such as a </span><span style="color:#0070c0; font-size:12px">diode or a JFET or a large signal amplifier</span></p>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:black">There is a </span><span style="color:#0070c0">nonlinear relationship </span><span style="color:black">between the input and output.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:black">The shape of the </span><span style="color:#0070c0">output waveform </span><span style="color:black">in the </span><span style="color:#0070c0">time-domain appears to be distorted.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:black">The </span><span style="color:#0070c0">waveform does not resemble </span><span style="color:black">with that of any input signal waveform.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="color:black">Infinite number of </span><span style="color:#0070c0">sum and difference</span><span style="color:black"> frequency components are produced.</span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:0pt; margin-left:.81in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="color:black"><strong>Frequency Division Multiplexing</strong></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="color:black; font-size:12px">Frequency Division Multiplexing (FDM) is the set </span><span style="color:#0070c0; font-size:12px">of techniques that allows the simultaneous transmission of many baseband signals </span><span style="color:black; font-size:12px">over a common communications channel.</span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.52pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">The individual baseband signals have </span><span style="color:#0070c0">identical frequency bands</span><span style="color:black">.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.52pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">To </span><span style="color:#0070c0">transmit</span><span style="color:black"> a number of such </span><span style="color:#0070c0">baseband signals </span><span style="color:black">over the same channel, the signals must be separated in frequency so that they do not interfere with each other.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.52pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">In </span><span style="color:#0070c0">FDM</span><span style="color:black">, each baseband signal is </span><span style="color:#0070c0">bandlimited</span><span style="color:black"> to <em>fm</em>, and then translated to different </span><span style="color:#0070c0">higher-frequency spectrum </span><span style="color:black">so that they do </span><span style="color:#0070c0">not overlap with each other</span><span style="color:black">.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.52pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="color:black">At the </span><span style="color:#0070c0">receiving end</span><span style="color:black">, the frequency-division multiplexed signal is applied to </span><span style="color:#0070c0">individual bandpass filters which pass only the desired baseband signals.</span></span></div>\r\n</div>\r\n</div>\r\n</div>\r\n', '', '', '1IA'),
('18EC751', '', 'Module 1', 'CO  1', 'KL 4', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Consider three voice signals, each having frequency range of 300 Hz&ndash;3400 Hz, are required to be frequency-division multiplexed using 12 kHz, 16 kHz, and 20 kHz analog carrier signals. Analyze the resultant spectrum at the output of FDM with the help of appropriate functional block diagram.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2290, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Resultant spectrum at the output of FDM with the help of appropriate functional block diagram</span></span></p>\r\n', '<span style="font-family:Times New Roman,Times,serif"><span style="font-size:12px">Figure&nbsp;illustrates a simplified arrangement of frequency-division multiplexing of three identical<br />\r\nvoice channels with their respective carrier signals.<br />\r\nThe output of Mixer 1 = 12 kHz &plusmn; (300 Hz &ndash; 3400 kHz)<br />\r\nThe output of LPF 1 = (12 kHz &ndash; 3400 Hz) to (12 kHz &ndash; 300 Hz)<br />\r\n= 8.6 kHz &ndash; 11.7 kHz</span></span></p>\r\n\r\n<p><span style="font-family:Times New Roman,Times,serif"><span style="font-size:12px"><img alt="" src="/svce/ece/qps/ckeditor/kcfinder/upload/images/Picture7.png" style="height:492px; width:866px" /></span></span></p>\r\n\r\n<p><span style="font-family:Times New Roman,Times,serif"><span style="font-size:12px">The output of Mixer 2 = 16 kHz &plusmn; (300 Hz &ndash; 3400 kHz)<br />\r\nThe output of LPF 2 = (16 kHz &ndash; 3400 Hz) to (16 kHz &ndash; 300 Hz)<br />\r\n= 12.6 kHz &ndash; 15.7 kHz<br />\r\nThe output of Mixer 3 = 20 kHz &plusmn; (300 Hz &ndash; 3400 kHz)<br />\r\nThe output of LPF 3 = (20kHz &ndash; 3400 Hz) to (20 kHz &ndash; 300 Hz)<br />\r\n= 16.6 kHz &ndash; 19.7 kHz<br />\r\nThe composite output spectrum is just the sum of the three frequency-translated signals. The spectrum of<br />\r\nresultant FDM signal is 8.6 kHz to 19.7 kHz frequency range. This illustrates the basic principle of FDM.</span></span></p>\r\n', '', '', '1IA'),
('18EC751', '', 'Module 1', 'CO  1', 'KL 3', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Illustrate briefly the concepts of radiations and antenna theory also discuss upon the types of antenna. </span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2291, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Concepts of radiations&nbsp;</span></span></p>\r\n\r\n<p><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Antenna theory </span></span></p>\r\n\r\n<p><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Types of antenna</span></span></p>\r\n', '<span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Concept of Radiation</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In </span><span style="color:#0070c0">wireless communications</span><span style="color:black">, the signals are transmitted in the form of </span><span style="color:#0070c0">electromagnetic waves </span><span style="color:black">through </span><span style="color:#0070c0">unguided wireless medium.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Transmitter antennas couple electromagnetic energy</span><span style="color:black"> from a coaxial cable connected to the transmitter in space for </span><span style="color:#0070c0">radiating the signal</span><span style="color:black">.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">receiver antenna </span><span style="color:black">can be used to receive the </span><span style="color:#0070c0">radio signals </span><span style="color:black">from the </span><span style="color:#0070c0">space and couples it to a coaxial cable </span><span style="color:black">connected to receiver.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Transmitter </span><span style="color:#0070c0">antenna gain </span><span style="color:black">is a measure of how well it </span><span style="color:#0070c0">emits the radiated energy </span><span style="color:black">in a certain area.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Receiver </span><span style="color:#0070c0">antenna gain </span><span style="color:black">is a measure of how well it </span><span style="color:#0070c0">collects the radiated energy</span><span style="color:black"> in that area.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Antenna Theory</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The antenna is an </span><span style="color:#0070c0">interface between</span></span> <span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">RF cable connected to transmitter/receiver</span><span style="color:black"> units and the space.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">primary function </span><span style="color:black">of transmitting antenna is to </span><span style="color:#0070c0">convert the electrical energy from a RF cable </span><span style="color:black">from a transmitter unit into electromagnetic waves in space.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">At the receiving antenna, the </span><span style="color:#0070c0">electric and magnetic fields in space cause current to flow </span><span style="color:black">in the conductors that make up the antenna.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Some of this </span><span style="color:#0070c0">energy is thereby transferred </span><span style="color:black">to the </span><span style="color:#0070c0">RF cable</span><span style="color:black"> connected to it and receiver unit.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Characteristics of Antennas</strong></span></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-family:Wingdings; font-size:12px">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;; font-size:12px"><span style="color:black">In general antennas are </span><span style="color:#0070c0">passive devices. </span><span style="color:black">This means the </span><span style="color:#0070c0">power radiated by a transmitting antenna</span></span><span style="font-size:12px"> </span><span style="font-family:&quot;Times New Roman&quot;; font-size:12px"><span style="color:#0070c0">cannot be greater than </span><span style="color:black">the </span><span style="color:#0070c0">power entering </span><span style="color:black">from the transmitter.</span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In fact the </span><span style="color:#0070c0">radiating power is always less than</span><span style="color:black"> the power at its input, because of losses.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Antenna gain in one direction </span><span style="color:black">results from a concentration of power and is accompanied by a </span><span style="color:#0070c0">loss</span><span style="color:black"> in other directions.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Antennas are </span><span style="color:#0070c0">reciprocal devices; </span><span style="color:black">that is, the same antenna design works equally well as a transmitting or a receiving antenna with </span><span style="color:#0070c0">the same amount of gain</span><span style="color:black">.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Types of Antenna</strong></span></span> </span></p>\r\n\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Various types of antennas </span><span style="color:#0070c0">differ in the amount of radiation they emit </span><span style="color:black">in various directions.</span></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Isotropic Antenna</strong></span></span></span></p>\r\n\r\n<p style="margin-left:0in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">An isotropic antenna is defined as a </span><span style="color:#0070c0">hypothetical loss-less antenna </span><span style="color:black">having equal radiation in all directions.</span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The actual </span><span style="color:#0070c0">radiation pattern </span><span style="color:black">for the </span><span style="color:#0070c0">isotropic antenna </span><span style="color:black">is a </span><span style="color:#0070c0">sphere</span><span style="color:black"> with the antenna at the center.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Radiation patterns are almost depicted as a </span><span style="color:#0070c0">two dimensional cross-section of the three dimensional pattern.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">An </span><span style="color:#0070c0">isotropic radiator </span><span style="color:black">is a theoretical </span><span style="color:#0070c0">perfect sphere </span><span style="color:black">that radiates power equally in all directions.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The closest thing to an isotropic radiator is the sun.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is </span><span style="color:#0070c0">not possible to build a real isotropic radiator </span><span style="color:black">because it would need a </span><span style="color:#0070c0">power cable connected </span><span style="color:black">to it at some point on the surface of the sphere.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">An </span><span style="color:#0070c0">isotropic radiator </span><span style="color:black">is taken as a </span><span style="color:#0070c0">reference</span><span style="color:black"> for expressing the </span><span style="color:#0070c0">directional properties of actual antennas</span><span style="color:black">.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Omnidirectional Antenna</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">An </span><span style="color:#0070c0">omnidirectional antenna </span><span style="color:black">allows transmission of radio signals with </span><span style="color:#0070c0">equal signal power in all directions</span><span style="color:black">. It is </span><span style="color:#0070c0">difficult to design </span><span style="color:black">omnidirectional antennas.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><strong style="color:black; font-family:&quot;Times New Roman&quot;; font-size:12px">Directional antennas</strong></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Directional antennas generally </span><span style="color:#0070c0">cover an area of 120 degrees or 60 degrees. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">A directional antenna, also called </span><span style="color:#0070c0">Yagi antenna</span><span style="color:black">, is one which has the property of </span><span style="color:#0070c0">radiating or receiving electromagnetic waves more effectively in some directions </span><span style="color:black">than in others.</span></span></span></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n', '', '', '1IA');
INSERT INTO `question_master2` (`course_code`, `CID`, `mo`, `co`, `kl`, `marks`, `question`, `r1`, `remarks1`, `r2`, `remarks2`, `r3`, `remarks3`, `final`, `q_id`, `question_number`, `qno`, `key1`, `scheme`, `cat2`, `ete`, `IA`) VALUES
('18EC751', '', 'Module 1', 'CO  1', 'KL 2', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Explain various antenna parameters.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2292, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Various antenna parameters</span></span></p>\r\n', '<span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Antenna Radiation Pattern</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is defined as a </span><span style="color:#0070c0">mathematical function or graphical representation of the radiation properties of the antenna </span><span style="color:black">as a function of the space coordinates.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">An antenna will </span><span style="color:#0070c0">radiate power in all directions </span><span style="color:black">but, typically, does not perform equally well in all directions.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">radiation pattern characterizes </span><span style="color:black">this performance of antenna.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Field Pattern</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">A graph of the </span><span style="color:#0070c0">spatial variation of the electric or magnetic field </span><span style="color:black">along a constant distance path is called a field pattern.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">distance from the antenna </span><span style="color:black">to each point on the </span><span style="color:#0070c0">radiation pattern</span><span style="color:black"> is proportional to the power radiated from the antenna in that direction.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">linear dipole </span><span style="color:black">is an example of a </span><span style="color:#0070c0">omnidirectional antenna</span><span style="color:black">, that is, an antenna having a radiation pattern which is non-directional in a plane.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Effective Isotropic Radiation Power (EIRP)</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">power radiated </span><span style="color:black">within a given geographic area is usually specified either with </span><span style="color:#0070c0">reference to isotropic antenna </span><span style="color:black">or an omnidirectional dipole antenna.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">effective isotropic radiated power </span><span style="color:black">is referenced to an </span><span style="color:#0070c0">isotropic antenna</span><span style="color:black">.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Directivity</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">directivity of a transmitting antenna </span><span style="color:black">is defined as the </span><span style="color:#0070c0">ratio of the radiation intensity flowing in a given direction to the radiation intensity</span><span style="color:black"> averaged over all direction.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Directivity is some times referred to as </span><span style="color:#0070c0">antenna gain.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is important to note that </span><span style="color:#0070c0">antenna gain does not refer to obtaining more output power</span><span style="color:black"> than input power but refer to directionality.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Absolute Gain</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">absolute gain </span><span style="color:black">of a </span><span style="color:#0070c0">transmitting antenna </span><span style="color:black">in a given direction is defined as the </span><span style="color:#0070c0">ratio of the radiation intensity </span><span style="color:black">flowing in that </span><span style="color:#0070c0">direction </span><span style="color:black">to the </span><span style="color:#0070c0">radiation intensity </span><span style="color:black">that would be obtained if the power accepted by the antenna were </span><span style="color:#0070c0">radiated isotropically.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#0070c0">Absolute gain </span><span style="color:black">of an antenna is closely related to its </span><span style="color:#0070c0">directivity.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It also takes into account the </span><span style="color:#0070c0">efficiency of antenna </span><span style="color:black">as well as its direction characteristics.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The </span><span style="color:#0070c0">absolute gain </span><span style="color:black">is some times </span><span style="color:#0070c0">referred to as power gain.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Relative Gain</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The relative gain of a transmitting antenna in a given direction is defined as the ratio of the absolute gain of the antenna in the given direction to the absolute gain of a reference antenna in the same direction. The power input to the two antennas must be the same.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Antenna gain</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Antenna gain is directional gain, not power gain, due to focusing of the radiated energy in specified direction.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Efficiency</strong></span></span> </span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The efficiency of a transmitting antenna is the ratio of the total radiated power radiated by the antenna to the input power to the antenna.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Antenna factor</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The antenna factor is the ratio of the magnitude of the electric field incident upon a receiving antenna to the voltage developed at the antenna&rsquo;s output connector</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The antenna factor is clearly related to the gain of antenna.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is often found to be the most convenient parameter for use in the monitoring of electromagnetic emissions.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Radiation Resistance</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The radiation resistance of a half-wave dipole antenna situated in free space and fed at the center is approximately 70 W.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The impedance is completely resistance at resonance.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It usually occurs when the length of the antenna is about 95% of the calculated free-space half wavelength value.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The exact length depends on the diameter of the antenna conductor relative to the operating wavelength.</span></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Polarization</strong></span></span> </span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The polarization of a radio wave is the orientation of its electric field vector.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It could be horizontal or vertical or hybrid.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is important that the polarization should be the same at both ends of a communication link.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Wireless communication systems usually use vertical polarization because this is more convenient for use with the portable and mobile antennas.</span></span> </span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Front-to-back Ratio</strong></span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The ratio between the gains to the front and back lobes is the front-to-back ratio.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Direction of maximum radiation in the horizontal plane is considered to be the front of the antenna, and the direction 180 degree from the front is considered to be its back.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Ratio of radiated power in intended direction to radiated power in opposite direction.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is generally expressed in dB.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">It is a measure of antenna&rsquo;s ability to focus radiated power in intended direction successfully, without interfering with other antennas behind it.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The dipole antenna viewed in the horizontal plane has two equal lobes of radiation.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The complex antenna has one major lobe and a number of minor lobes. Each of these lobes has a gain and beamwidth.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Effective Area or Aperture of a Receiving Antenna</strong></span></span></span></p>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-family:Wingdings; font-size:12px">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;; font-size:12px"><span style="color:black">It is defined as the ratio of the available power at the terminals of the antenna to the radiation intensity of a plane wave incident on the antenna in the given direction.</span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Wingdings">&Oslash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In fact, the effective area of an antenna is related to the physical size of the antenna and its shape.</span></span></span></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 1', 5, '<p><span style="font-size:12.0pt">Explain the four fundamental characteristics that provide effectiveness to data communications system. </span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2293, '<span style="font-size:25.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">1. Delivery</span></span></span></p>\r\n\r\n<p><span style="font-size:25.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">2. Accuracy</span></span></span></p>\r\n\r\n<p><span style="font-size:25.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">3. Timeliness</span></span></span></p>\r\n\r\n<p><span style="font-size:25.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">4. Jitter</span></span></span></p>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>1. Delivery. </strong>The system must deliver data to the correct destination. Data must be received by the intended device or user and only by that device or user.&nbsp; &nbsp; 1M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>2. Accuracy. </strong>The system must deliver the data accurately. Data that have been altered in transmission and left uncorrected are unusable.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>3. Timeliness. </strong>The system must deliver data in a timely manner. Data delivered late are useless. In the case of video and audio, timely delivery means delivering data as they are produced, in the same order that they are produced, and without significant delay. This kind of delivery is called real-time transmission.&nbsp; &nbsp; 1.5M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>4. Jitter. </strong>Jitter refers to the variation in the packet arrival time. It is the uneven delay in the delivery of audio or video packets. For example, let us assume that video packets are sent every 30 ms. If some of the packets arrive with 30-ms delay and others with 40-ms delay, an uneven quality in the video is the result.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1.5M</span></span></div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 2', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Identify the five components of a data communications system. </span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2294, '<span style="font-size:22.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">text, numbers, images, audio, </span></span></span><span style="font-size:22.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">and video.</span></span></span></p>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:22.0pt"><span style="font-family:Arial">&bull;</span></span><span style="font-size:12px"><span style="color:black"><strong><em>Text</em></strong></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="color:black">&nbsp;&nbsp;&nbsp;&nbsp; In data communications, text is represented as a bit pattern, a sequence of bits (0s or 1s). Different sets of bit patterns have been designed to represent text symbols. Each set is called a <strong>code, </strong>and the process of representing symbols is called coding. Today, the prevalent coding system is called <strong>Unicode, </strong>which uses 32 bits to represent a symbol or character used in any language in the world. The <strong>American Standard Code for Information Interchange (ASCII), </strong>developed some decades ago in the United States, now constitutes the first 127 characters in Unicode and is also referred to as <strong>Basic Latin.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2M</strong></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong><em>Numbers</em></strong></span></span></div>\r\n\r\n<p style="margin-left:.38in; text-align:justify"><span style="font-size:12px"><span style="color:black">&nbsp; Numbers are also represented by bit patterns. However, a code such as ASCII is not used to represent numbers; the number is directly converted to a binary number to simplify mathematical operations. Appendix B discusses several different numbering systems.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2M</span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:20.0pt"><span style="font-family:Arial">&bull;</span></span><span style="font-size:12px"><span style="color:black"><strong>Images</strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black">Images are also represented by bit patterns. In its simplest form, an image is composed of a matrix of pixels (picture elements), where each pixel is a small dot. The size of the pixel depends on the resolution. For example, an image can be divided into 1000 pixels or 10,000 pixels. In the second case, there is a better representation of the image better resolution), but more memory is needed to store the image. </span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black">After an image is divided into pixels, each pixel is assigned a bit pattern. The size and the value of the pattern depend on the image. For an image made of only black and- white dots (e.g., a chessboard), a 1-bit pattern is enough to represent a pixel.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:27.0pt"><span style="font-family:Arial">&bull;</span></span><span style="font-size:12px"><span style="color:black"><strong><em>Audio</em></strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>Audio </strong>refers to the recording or broadcasting of sound or music. Audio is by nature different from text, numbers, or images. It is continuous, not discrete. Even when we use a microphone to change voice or music to an electric signal, we create a continuous signal.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.48pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong><em>Video</em></strong></span></span></div>\r\n<span style="font-size:12px"><span style="color:black"><strong>Video </strong>refers to the recording or broadcasting of a picture or movie. Video can either be produced as a continuous entity (e.g., by a TV camera), or it can be a combination of images, each a discrete entity, arranged to convey the idea of motion.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2M</span></span></div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 2', 5, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Explain Data Representation with examples.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2295, '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Communication between two devices can be simplex, half-duplex, and&nbsp;full-duplex&nbsp;</span></span></span></div>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black">Communication between two devices can be simplex&nbsp; &nbsp; 1.5M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left"><span style="font-size:12px"><span style="color:black">&nbsp;half-duplex&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1.5M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left"><span style="font-size:12px"><span style="color:black">&nbsp;full-duplex&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left">&nbsp;</div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left">&nbsp;</div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.68pt; text-align:left">&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 1', 5, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">What are the three criteria necessary for an effective and efficient network? </span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2296, '<div style="margin-bottom:6.0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>performance, reliability, and security.</strong></span></span></span></div>\r\n', '<div style="margin-bottom:6.0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>Network Criteria: </strong>A network must be able to meet a certain number of criteria. The most important of these are <strong>performance, reliability, and security.</strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong><em>Performance</em></strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>Performance </strong>can be measured in many ways, including transit time and response time. Transit time is the amount of time required for a message to travel from one device to another. Response time is the elapsed time between an inquiry and a response. The performance of a network depends on a number of factors, including the number of users, the type of transmission medium, the capabilities of the connected hardware, and the efficiency of the software. </span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black">Performance is often evaluated by two networking metrics: <strong>throughput </strong>and <strong>delay. </strong>We often need more throughput and less delay. However, these two criteria are often contradictory. If we try to send more data to the network, we may increase throughput but we increase the delay because of traffic congestion in the network.&nbsp; &nbsp; &nbsp;2M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:30.0pt"><span style="font-family:Arial">&bull;</span></span><span style="font-size:12px"><span style="color:black"><strong><em>Reliability</em></strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black">In addition to accuracy of delivery, network <strong>reliability </strong>is measured by the frequency of failure, the time it takes a link to recover from a failure, and the network&rsquo;s robustness in a catastrophe.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1.5M</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong><em>Security</em></strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:7.2pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black">Network <strong>security </strong>issues include protecting data from unauthorized access, protecting data from damage and development, and implementing policies and procedures for recovery from breaches and data losses.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1.5M</span></span></div>\r\n</div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 2', 5, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Explain the two possible types of connections. </span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2297, '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:left"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Type of Connection: </em></strong>There are two possible types of connections: <strong>point-to-point and multipoint.</strong></span></span></span></div>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:left"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong>Physical Structures: </strong>Before discussing networks, we need to define some network attributes.</span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:left"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="color:black"><strong><em>Type of Connection: </em></strong>There are two possible types of connections: <strong>point-to-point&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.5M</strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:left"><span style="font-size:12px"><span style="color:black"><strong>&nbsp;multipoint.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.5M</strong></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:left">&nbsp;</div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 4', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Categorize the four basic topologies in terms of line configuration.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2298, '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Physical Topology: </em></strong>There are four basic topologies possible: <strong>mesh, star, bus, and ring.</strong></span></span></span></div>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Mesh Topology: </em></strong></span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In a <strong>mesh topology, </strong>every device has a dedicated point-to-point link to every other device. The term <em>dedicated </em>means that the link carries traffic only between the two devices it connects. To find the number of physical links in a fully connected mesh network with <em>n </em>nodes, we first consider that each node must be connected to every other node. Node 1 must be connected to <em>n </em>&ndash; 1 nodes, node 2 must be connected to <em>n </em>&ndash; 1 nodes, and finally node <em>n </em>must be connected to <em>n </em>&ndash; 1 nodes. We need <em>n </em>(<em>n </em>&ndash; 1) physical links. However, if each physical link allows communication in both directions (duplex mode), we can divide the number of links by 2. In other words, we can say that in a mesh topology, we need <strong><em>n </em>(<em>n </em>&ndash; 1) </strong>/ <strong>2 </strong>duplex-mode links. To accommodate that many links, every device on the network must have <em>n </em>&ndash; 1 input/output (I/O) ports (see Figure 1.4) to be connected to the other <em>n </em>&ndash; 1 stations.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2.5M</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.28pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:left"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Star Topology</em></strong></span></span></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In a <strong>star topology, </strong>each device has a dedicated point-to-point link only to a central controller, usually called a <strong><em>hub</em>. </strong>The devices are not directly linked to one another. Unlike a mesh topology, a star topology does not allow direct traffic between devices. The controller acts as an exchange: If one device wants to send data to another, it sends the data to the controller, which then relays the data to the other connected device&nbsp; &nbsp; &nbsp; &nbsp; 2.5M</span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:6.72pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Bus Topology</em></strong></span></span></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The preceding examples all describe point-to-point connections. A <strong>bus topology, </strong>on the other hand, is multipoint. One long cable acts as a <strong>backbone </strong>to link all the devices in a network&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.5M</span></span></span></p>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:4.8pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Ring Topology</em></strong></span></span></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; In a <strong>ring topology, </strong>each device has a dedicated point-to-point connection with only the two devices on either side of it. A signal is passed along the ring in one direction, from device to device, until it reaches its destination. Each device in the ring incorporates a repeater. When a device receives a signal intended for another device, its repeater regenerates the bits and passes them along&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.5M</span></span></span></p>\r\n</div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 1', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Explain LAN, WAN and Internetwork.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2299, '<span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Local Area Network</strong></span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Wide Area Network: </strong>A <strong>wide area network (WAN)</strong></span></span> </span><span style="font-size:28.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><span style="font-size:12px">is also an interconnection of devices capable of communication.</span> </span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Internetwork: </strong></span></span></span><span style="font-size:18.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><span style="font-size:12px">Today, it is very rare to see a LAN or a WAN in isolation; they are connected to one another. When two or more networks are connected, they make an internetwork, or internet.</span> </span></span></span></p>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">We use a few criteria such as <strong><em>size, geographical coverage, and ownership </em></strong>to make this distinction. After discussing two types of networks, <strong><em>LANs and WANs</em></strong>, we define <strong><em>switching</em></strong>, which is used to connect networks to form an <strong><em>internetwork</em></strong> (a network of networks).</span></span></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Local Area Network: </strong>A local area network (LAN) is usually privately owned and connects some hosts in a single office, building, or campus. Depending on the needs of an organization, a LAN can be as simple as two PCs and a printer in someone&rsquo;s home office, or it can extend throughout a company and include audio and video devices. Each host in a LAN has an identifier, an address, that uniquely defines the host in the LAN. A packet sent by a host to another host carries both the source host&rsquo;s and the destination host&rsquo;s addresses.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4M</span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Point-to-Point WAN: </strong>A point-to-point WAN is a network that connects two communicating devices through a transmission media (cable or air).</span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Switched WAN: </strong>A switched WAN is a network with more than two ends. A switched WAN, as we will see shortly, is used in the backbone of global communication today. We can say that a switched WAN is a combination of several point-to-point WANs that are connected by switches.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4M</span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Internetwork: </strong></span></span></span><span style="font-size:18.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><span style="font-size:12px">Today, it is very rare to see a LAN or a WAN in isolation; they are connected to one another. When two or more networks are connected, they make an internetwork, or internet.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2M</span></span></span></span></p>\r\n', '', '', '1IA');
INSERT INTO `question_master2` (`course_code`, `CID`, `mo`, `co`, `kl`, `marks`, `question`, `r1`, `remarks1`, `r2`, `remarks2`, `r3`, `remarks3`, `final`, `q_id`, `question_number`, `qno`, `key1`, `scheme`, `cat2`, `ete`, `IA`) VALUES
('18EC71', '', 'Module 1', 'CO  1', 'KL 2', 5, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Explain two most common types of switched networks.</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2300, '<span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Circuit-Switched Network</em></strong></span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Packet-Switched Network</em></strong></span></span></span></p>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Circuit-Switched Network: </em></strong>In a <strong>circuit-switched network, </strong>a dedicated connection, called a circuit, is always available between the two end systems; the switch can only make it active or inactive. Figure shows a very simple switched network that connects four telephones to each end. We have used telephone sets instead of computers as an end system because circuit switching was very common in telephone networks in the past, although part of the telephone network today is a packet-switched network.&nbsp; &nbsp; &nbsp;2.5M</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Packet-Switched Network: </em></strong>In a computer network, the communication between the two ends is done in blocks of data called <strong>packets. </strong></span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In other words, instead of the continuous communication we see between two telephone sets when they are being used, we see the exchange of individual data packets between the two computers. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">This allows us to make the switches function for both storing and forwarding because a packet is an independent entity that can be stored and sent later.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.5M</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">&nbsp;</div>\r\n</div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 3', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Distinguish between the Circuit-Switched Network with the Packet-Switched Network. </span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2301, '<span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Circuit-Switched Network</em></strong></span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Packet-Switched Network</em></strong></span></span></span></p>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Circuit-Switched Network: </em></strong>In a <strong>circuit-switched network, </strong>a dedicated connection, called a circuit, is always available between the two end systems; the switch can only make it active or inactive. Figure shows a very simple switched network that connects four telephones to each end. We have used telephone sets instead of computers as an end system because circuit switching was very common in telephone networks in the past, although part of the telephone network today is a packet-switched network.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Let us look at two cases. In the first case, all telephone sets are busy; four people at one site are talking with four people at the other site; the capacity of the thick line is fully used. In the second case, only one telephone set at one side is connected to a telephone set at the other side; only one-fourth of the capacity of the thick line is used. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">This means that a circuit-switched network is efficient only when it is working at its full capacity; most of the time, it is inefficient because it is working at partial capacity.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5M</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Packet-Switched Network: </em></strong>In a computer network, the communication between the two ends is done in blocks of data called <strong>packets. </strong></span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">In other words, instead of the continuous communication we see between two telephone sets when they are being used, we see the exchange of individual data packets between the two computers. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">This allows us to make the switches function for both storing and forwarding because a packet is an independent entity that can be stored and sent later.&nbsp; &nbsp; &nbsp; 5M</span></span></span></div>\r\n</div>\r\n</div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 3', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Genralise how Internet working&nbsp;Today</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2302, '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>The Internet: </strong>an internet is two or more networks that can communicate with each other. The most notable internet is called the <strong>Internet </strong>(uppercase <em>I </em>), and is composed of thousands of interconnected networks.</span></span></span></div>\r\n', '<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:14px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>The Internet: </strong>an internet is two or more networks that can communicate with each other. The most notable internet is called the <strong>Internet </strong>(uppercase <em>I </em>), and is composed of thousands of interconnected networks.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:14px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">At the top level, the <em>backbones </em>are large networks owned by some communication companies such as Sprint, Verizon (MCI), AT&amp;T, and NTT. The backbone networks are connected through some complex switching systems, called <em>peering points</em>. At the second level, there are smaller networks, called <em>provider networks</em>, that use the services of the backbones for a fee. The provider networks are connected to backbones and sometimes to other provider networks. </span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:14px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">The <em>customer networks </em>are networks at the edge of the Internet that actually use the services provided by the Internet. They pay fees to provider networks for receiving services. Backbones and provider networks are also called <strong>Internet Service Providers (ISPs). </strong>The backbones are often referred to as <em>international ISPs; </em>the provider networks are often referred to as <em>national </em>or <em>regional ISPs</em>.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 10M</span></span></span></div>\r\n', '', '', '1IA'),
('18EC71', '', 'Module 1', 'CO  1', 'KL 4', 10, '<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Analyse how to Accessing the Internet</span></span></p>\r\n', 'Y', '', '', '', '', '', '', 0, 0, 2303, '<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Using Telephone Networks</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Using Cable Networks</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Using Wireless Networks</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Direct Connection to the Internet</em></strong></span></span></span></div>\r\n', '<div style="margin-bottom:6.0pt; margin-left:.38in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Accessing the Internet: </strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Using Telephone Networks&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2.5M</em></strong></span></span></span></div>\r\n\r\n<div class="O3" style="margin-bottom:6.0pt; margin-left:1.75in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Dial-up service</em></strong></span></span></span></div>\r\n\r\n<div class="O3" style="margin-bottom:6.0pt; margin-left:1.75in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>DSL Service</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Using Cable Networks&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.5M</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Using Wireless Networks&nbsp; &nbsp; &nbsp; &nbsp; 2.5M</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&ndash;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Direct Connection to the Internet&nbsp; &nbsp; &nbsp; 2.5M</em></strong></span></span></span></div>\r\n\r\n<div class="O1" style="margin-bottom:6.0pt; margin-left:.81in; margin-top:6.0pt; text-align:justify">\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong>Internet Today</strong></span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">Today, we witness a rapid growth both in the infrastructure and new applications. The Internet today is a set of pier networks that provide services to the whole world. What has made the Internet so popular is the invention of new applications.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>World Wide Web: </em></strong>The 1990s saw the explosion of Internet applications due to the emergence of the World Wide Web (WWW). The Web was invented at CERN by Tim Berners-Lee. This invention has added the commercial applications to the Internet.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Multimedia: </em></strong>Recent developments in the multimedia applications such as voice over IP (telephony), video over IP (Skype), view sharing (YouTube), and television over IP (PPLive) has increased the number of users and the amount of time each user spends on the network.</span></span></span></div>\r\n\r\n<div style="margin-bottom:0pt; margin-left:.38in; margin-top:5.76pt; text-align:justify"><span style="font-size:12px"><span style="font-family:Arial">&bull;</span><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><strong><em>Peer-to-Peer Applications: </em></strong>Peer-to-peer networking is also a new area of communication with a lot of potential.</span></span></span></div>\r\n</div>\r\n\r\n<p style="margin-left:.22in; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:.22in; text-align:justify">&nbsp;</p>\r\n', '', '', '1IA'),
